import React, { Component } from 'react';

class Jokes extends Component {
    state = {joke:{}, jokes:[] };

    componentDidMount() {
        // store any fetches or asyncronishe code in here 
        fetch('https://official-joke-api.appspot.com/random_joke').then(response => response.json()).then(json => this.setState({joke: json}))
        // or
        // fetch('https://official-joke-api.appspot.com/random_joke').then(response => { return response.json() };
    }

    fetchJokes = () => {
        fetch('https://official-joke-api.appspot.com/random_joke').then(response => response.json()).then(json => this.setState({joke: json}))
    }

    render() {
        const {setup,punchline} = this.state.joke;
        return (
            <div>
            <h2> Ten Highlighted Jokes</h2>
             <p id="jokes">{setup}<em>{punchline}</em></p> 
            <button></button>
            </div>
        )
    }
}

export default Jokes;
